import os

from datasets import load_dataset
from gensim.corpora import Dictionary
from gensim.models import LdaModel
from sklearn.feature_extraction.text import ENGLISH_STOP_WORDS

from scripts.feat_topic_lda import load_corpus

LDA_BASEDIR = os.path.join("data", "topics", "lda")
LDA_MODEL_FILE = os.path.join(LDA_BASEDIR, "lda.gensim")
DICTIONARY_FILE = os.path.join(LDA_BASEDIR, "lda.dict")


def run():
    dictionary = Dictionary.load(DICTIONARY_FILE)
    lda_model = LdaModel.load(LDA_MODEL_FILE)
    texts = load_corpus("PsyMatrix", "train")
    print("corpus loaded")

    # topics_id = 69
    # print(lda_model.show_topic(69, topn=10))

    # Calculate the topic coherence
    coherence = lda_model.top_topics(
        dictionary=dictionary, texts=texts, coherence="c_uci", topn=10
    )

    for coherence_score, topic in coherence:
        print(coherence_score, topic)


if __name__ == "__main__":
    run()


